import { useState, useEffect } from 'react';
import Axios from 'axios';
import './App.css';

function App() {
  const [productName, setProductName] = useState('');
  const [days, setDays] = useState(0);
  const [newProductName, setNewProductName] = useState('');

  const [productList, setProductList] = useState([]);

  useEffect(() => {
    Axios.get('http://localhost:3001/read').then((response) => {
      setProductList(response.data);
    });
  }, []);

  const addToList = () => {
    Axios.post('http://localhost:3001/insert', {
      productName: productName,
      days: days,
    });
  };

  const updateProduct = (id) => {
    Axios.put('http://localhost:3001/update', {
      id: id, newProductName: newProductName
    });
  };

  const deleteProduct = (id) => {
    Axios.delete(`http://localhost:3001/delete/${id}`);
  };

  return (
    <div className="App">
      <h1>Product Management System Releasin</h1>
    <div className='container__type'>
      <h2>Create Product Type</h2>
      <label>Product Type Name:</label>
        <input
          type="text"
          // onChange={(event) => {
          //   setProductName(event.target.value);
          // }}
        />
    </div>

    <div className='container__product'>
      <div>
        <label>Product Name:</label>
      <input
        type="text"
        onChange={(event) => {
          setProductName(event.target.value);
        }}
      />
      </div>

      <div>

      </div>
      <label>Days Since:</label>
      <input
        type="number"
        onChange={(event) => {
          setDays(event.target.value);
        }}
      />
      <button onClick={addToList}>Add to list</button>
    </div>


      <h1>Product List</h1>

      {productList.map((val, key) => {
        return (
          <div className="product" key={key}>
            <h1> {val.productName} </h1> <h1>{val.daysSinceIAte}</h1>
            <input
              type="text"
              placeholder="New Product Name..."
              onChange={(event) => {
                setNewProductName(event.target.value);
              }}
            />
            <button onClick={() => updateProduct(val._id)}>Update</button>
            <button onClick={()=> deleteProduct(val._id)}>Delete</button>
          </div>
        );
      })}
    </div>
  );
}

export default App;
