import { useState, useEffect } from 'react';
import Axios from 'axios';
import './App.css';

function App() {
  const [foodName, setFoodName] = useState("");
  const [days, setDays] = useState(0);
  const [newFoodName, setNewFoodName] = useState("")

  const [foodList, setFoodList] = useState([])

  useEffect(() => {
    Axios.get('http://localhost:3001/read').then((response) => {
      setFoodList(response.data)
    })
  }, [])

  const addToList = () => {
    Axios.post("http://localhost:3001/insert", {
      foodName: foodName, 
      days: days,
    });
  }

  return (
    <div className="App">
      <h1>Crud App with MERN</h1>

      <label>Food Name:</label>
      <input type="text" onChange={(event) => {setFoodName(event.target.value)}}/>
      <label>Days Since:</label>
      <input type="number" onChange={(event) => {setDays(event.target.value)}}/>
      <button onClick={addToList}>Add to list</button>

      <h1>food List</h1>

      {foodList.map((val, key) => {
        return <div className='product' key={key}> 
                  <h1> {val.foodName} </h1> <h1>{val.daysSinceIAte}</h1> 
                  <input type="text" placeholder='New Food Name...' onChange={(event) => {
                    setNewFoodName
                  }}/>
                  <button>Update</button>
                  <button>Delete</button>
               </div>
      })}
    </div>
  );
}

export default App;
